// src/contexts/ProductContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { productApi, categoryApi } from '../services/api';

// Tạo context
const ProductContext = createContext();

// Hook để sử dụng context
export const useProduct = () => useContext(ProductContext);

// Provider component
export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  function AdminDashboard({ onLogout }) {
    const navigate = useNavigate();
  }

  // Khởi tạo dữ liệu
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setIsLoading(true);
        
        // Lấy giỏ hàng và danh sách yêu thích từ localStorage
        const savedCart = localStorage.getItem('cart');
        const savedWishlist = localStorage.getItem('wishlist');
        
        if (savedCart) {
          setCart(JSON.parse(savedCart));
        }
        
        if (savedWishlist) {
          setWishlist(JSON.parse(savedWishlist));
        }
        
        // Lấy danh sách danh mục
        const categoriesResponse = await categoryApi.getAll();
        setCategories(categoriesResponse.data);
        
        // Lấy danh sách sản phẩm
        const productsResponse = await productApi.getAll();
        setProducts(productsResponse.data);
        
        setError(null);
      } catch (err) {
        console.error('Lỗi khi khởi tạo dữ liệu:', err);
        setError('Không thể tải dữ liệu ban đầu. Vui lòng tải lại trang.');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchInitialData();
  }, []);

  // Lưu giỏ hàng vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading) {
      localStorage.setItem('cart', JSON.stringify(cart));
    }
  }, [cart, isLoading]);

  // Lưu danh sách yêu thích vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading) {
      localStorage.setItem('wishlist', JSON.stringify(wishlist));
    }
  }, [wishlist, isLoading]);

  // Thêm sản phẩm vào giỏ hàng
  const addToCart = (product, quantity = 1) => {
    const existingItemIndex = cart.findIndex(item => item.id === product.id);
    
    if (existingItemIndex !== -1) {
      // Cập nhật số lượng nếu sản phẩm đã có trong giỏ hàng
      const updatedCart = [...cart];
      updatedCart[existingItemIndex].quantity += quantity;
      setCart(updatedCart);
    } else {
      // Thêm sản phẩm mới vào giỏ hàng
      setCart([...cart, { ...product, quantity }]);
    }
  };

  // Cập nhật số lượng sản phẩm trong giỏ hàng
  const updateCartItem = (productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
      return;
    }
    
    const updatedCart = cart.map(item => 
      item.id === productId ? { ...item, quantity } : item
    );
    setCart(updatedCart);
  };

  // Xóa sản phẩm khỏi giỏ hàng
  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  // Xóa toàn bộ giỏ hàng
  const clearCart = () => {
    setCart([]);
  };

  // Thêm vào danh sách yêu thích
  const addToWishlist = (product) => {
    if (!wishlist.some(item => item.id === product.id)) {
      setWishlist([...wishlist, product]);
    }
  };

  // Xóa khỏi danh sách yêu thích
  const removeFromWishlist = (productId) => {
    setWishlist(wishlist.filter(item => item.id !== productId));
  };

  // Tính tổng giá trị giỏ hàng
  const getCartTotal = () => {
    return cart.reduce((total, item) => {
      const price = item.discount_price || item.price;
      return total + (price * item.quantity);
    }, 0);
  };

  // Lấy sản phẩm theo ID
  const getProductById = async (id) => {
    try {
      const response = await productApi.getById(id);
      return response.data;
    } catch (err) {
      console.error(`Lỗi khi lấy thông tin sản phẩm ${id}:`, err);
      throw err;
    }
  };

  // Lấy sản phẩm theo danh mục
  const getProductsByCategory = async (categoryId) => {
    try {
      const response = await productApi.getByCategory(categoryId);
      return response.data;
    } catch (err) {
      console.error(`Lỗi khi lấy sản phẩm theo danh mục ${categoryId}:`, err);
      throw err;
    }
  };

  // Tìm kiếm sản phẩm
  const searchProducts = async (query) => {
    try {
      const response = await productApi.search(query);
      return response.data;
    } catch (err) {
      console.error('Lỗi khi tìm kiếm sản phẩm:', err);
      throw err;
    }
  };

  // Các giá trị và hàm được cung cấp qua context
  const value = {
    products,
    categories,
    cart,
    wishlist,
    isLoading,
    error,
    addToCart,
    updateCartItem,
    removeFromCart,
    clearCart,
    addToWishlist,
    removeFromWishlist,
    getCartTotal,
    getProductById,
    getProductsByCategory,
    searchProducts
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};
