// src/contexts/ProductContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { productApi, categoryApi } from '../services/api';
import productData from '../data/productData'; // Import dữ liệu mẫu

// Tạo context
const ProductContext = createContext();

// Hook để sử dụng context
export const useProduct = () => useContext(ProductContext);

// Provider component
export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Khởi tạo dữ liệu
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setIsLoading(true);
        
        // Lấy dữ liệu sản phẩm từ localStorage nếu có
        const savedProducts = localStorage.getItem('products');
        
        // Lấy giỏ hàng và danh sách yêu thích từ localStorage
        const savedCart = localStorage.getItem('cart');
        const savedWishlist = localStorage.getItem('wishlist');
        
        if (savedCart) {
          setCart(JSON.parse(savedCart));
        }
        
        if (savedWishlist) {
          setWishlist(JSON.parse(savedWishlist));
        }
        
        // Lấy danh sách danh mục
        try {
          const categoriesResponse = await categoryApi.getAll();
          if (categoriesResponse.data && categoriesResponse.data.length > 0) {
            setCategories(categoriesResponse.data);
          }
        } catch (categoryError) {
          console.error('Không thể lấy danh mục từ API:', categoryError);
        }
        
        // Lấy danh sách sản phẩm
        try {
          if (savedProducts) {
            // Sử dụng dữ liệu từ localStorage nếu có
            setProducts(JSON.parse(savedProducts));
          } else {
            // Thử lấy từ API
            const productsResponse = await productApi.getAll();
            if (productsResponse.data && productsResponse.data.length > 0) {
              setProducts(productsResponse.data);
            } else {
              // Sử dụng dữ liệu mẫu nếu không có dữ liệu từ API
              setProducts(productData);
            }
          }
        } catch (productError) {
          console.error('Không thể lấy sản phẩm từ API:', productError);
          // Sử dụng dữ liệu mẫu nếu có lỗi
          setProducts(productData);
        }
        
        setError(null);
      } catch (err) {
        console.error('Lỗi khi khởi tạo dữ liệu:', err);
        setError('Không thể tải dữ liệu ban đầu. Vui lòng tải lại trang.');
        // Sử dụng dữ liệu mẫu trong trường hợp lỗi
        setProducts(productData);
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchInitialData();
  }, []);

  // Lưu sản phẩm vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading && products.length > 0) {
      localStorage.setItem('products', JSON.stringify(products));
    }
  }, [products, isLoading]);

  // Lưu giỏ hàng vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading) {
      localStorage.setItem('cart', JSON.stringify(cart));
    }
  }, [cart, isLoading]);

  // Lưu danh sách yêu thích vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading) {
      localStorage.setItem('wishlist', JSON.stringify(wishlist));
    }
  }, [wishlist, isLoading]);

  // Thêm sản phẩm mới
  const addProduct = (product) => {
    try {
      // Thêm sản phẩm vào state
      setProducts([...products, product]);
      
      // Gửi dữ liệu lên server nếu có thể
      try {
        productApi.create(product);
      } catch (apiError) {
        console.error('Lỗi khi gọi API thêm sản phẩm:', apiError);
        // Không làm gì nếu API lỗi, vì sản phẩm đã được thêm vào state
      }
      
      return product;
    } catch (error) {
      console.error('Lỗi khi thêm sản phẩm:', error);
      throw error;
    }
  };

  // Cập nhật sản phẩm
  const updateProduct = (id, updatedData) => {
    try {
      // Cập nhật sản phẩm trong state
      const updatedProducts = products.map(product => 
        product.id === id ? { ...product, ...updatedData } : product
      );
      setProducts(updatedProducts);
      
      // Gửi dữ liệu lên server nếu có thể
      try {
        productApi.update(id, updatedData);
      } catch (apiError) {
        console.error('Lỗi khi gọi API cập nhật sản phẩm:', apiError);
        // Không làm gì nếu API lỗi, vì sản phẩm đã được cập nhật trong state
      }
      
      // Cập nhật sản phẩm trong giỏ hàng nếu có
      if (cart.some(item => item.id === id)) {
        const updatedCart = cart.map(item => 
          item.id === id ? { ...item, ...updatedData, quantity: item.quantity } : item
        );
        setCart(updatedCart);
      }
      
      // Cập nhật sản phẩm trong danh sách yêu thích nếu có
      if (wishlist.some(item => item.id === id)) {
        const updatedWishlist = wishlist.map(item => 
          item.id === id ? { ...item, ...updatedData } : item
        );
        setWishlist(updatedWishlist);
      }
      
      return updatedProducts.find(product => product.id === id);
    } catch (error) {
      console.error('Lỗi khi cập nhật sản phẩm:', error);
      throw error;
    }
  };

  // Xóa sản phẩm
  const deleteProduct = (id) => {
    try {
      // Xóa sản phẩm khỏi state
      setProducts(products.filter(product => product.id !== id));
      
      // Gửi yêu cầu xóa lên server nếu có thể
      try {
        productApi.delete(id);
      } catch (apiError) {
        console.error('Lỗi khi gọi API xóa sản phẩm:', apiError);
        // Không làm gì nếu API lỗi, vì sản phẩm đã được xóa khỏi state
      }
      
      // Xóa sản phẩm khỏi giỏ hàng nếu có
      if (cart.some(item => item.id === id)) {
        setCart(cart.filter(item => item.id !== id));
      }
      
      // Xóa sản phẩm khỏi danh sách yêu thích nếu có
      if (wishlist.some(item => item.id === id)) {
        setWishlist(wishlist.filter(item => item.id !== id));
      }
      
      return id;
    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm:', error);
      throw error;
    }
  };

  // Thêm sản phẩm vào giỏ hàng
  const addToCart = (product, quantity = 1) => {
    const existingItemIndex = cart.findIndex(item => item.id === product.id);
    
    if (existingItemIndex !== -1) {
      // Cập nhật số lượng nếu sản phẩm đã có trong giỏ hàng
      const updatedCart = [...cart];
      updatedCart[existingItemIndex].quantity += quantity;
      setCart(updatedCart);
    } else {
      // Thêm sản phẩm mới vào giỏ hàng
      setCart([...cart, { ...product, quantity }]);
    }
  };

  // Cập nhật số lượng sản phẩm trong giỏ hàng
  const updateCartItem = (productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
      return;
    }
    
    const updatedCart = cart.map(item => 
      item.id === productId ? { ...item, quantity } : item
    );
    setCart(updatedCart);
  };

  // Xóa sản phẩm khỏi giỏ hàng
  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  // Xóa toàn bộ giỏ hàng
  const clearCart = () => {
    setCart([]);
  };

  // Thêm vào danh sách yêu thích
  const addToWishlist = (product) => {
    if (!wishlist.some(item => item.id === product.id)) {
      setWishlist([...wishlist, product]);
    }
  };

  // Xóa khỏi danh sách yêu thích
  const removeFromWishlist = (productId) => {
    setWishlist(wishlist.filter(item => item.id !== productId));
  };

  // Tính tổng giá trị giỏ hàng
  const getCartTotal = () => {
    return cart.reduce((total, item) => {
      const price = item.discountPrice || item.price;
      return total + (price * item.quantity);
    }, 0);
  };

  // Lấy sản phẩm theo ID
  const getProductById = (id) => {
    // Tìm sản phẩm trong state trước
    const product = products.find(product => product.id === parseInt(id));
    if (product) {
      return Promise.resolve({ data: product });
    }
    
    // Nếu không tìm thấy, thử lấy từ API
    try {
      return productApi.getById(id);
    } catch (err) {
      console.error(`Lỗi khi lấy thông tin sản phẩm ${id}:`, err);
      return Promise.reject(err);
    }
  };

  // Lấy sản phẩm theo danh mục
  const getProductsByCategory = (categoryName) => {
    // Lọc sản phẩm theo danh mục từ state
    const filteredProducts = products.filter(product => 
      product.category.toLowerCase() === categoryName.toLowerCase()
    );
    
    return Promise.resolve({ data: filteredProducts });
  };

  // Tìm kiếm sản phẩm
  const searchProducts = (query) => {
    // Tìm kiếm sản phẩm từ state
    const searchResults = products.filter(product => 
      product.name.toLowerCase().includes(query.toLowerCase()) ||
      product.description.toLowerCase().includes(query.toLowerCase()) ||
      product.category.toLowerCase().includes(query.toLowerCase())
    );
    
    return Promise.resolve({ data: searchResults });
  };

  // Các giá trị và hàm được cung cấp qua context
  const value = {
    products,
    categories,
    cart,
    wishlist,
    isLoading,
    error,
    addToCart,
    updateCartItem,
    removeFromCart,
    clearCart,
    addToWishlist,
    removeFromWishlist,
    getCartTotal,
    getProductById,
    getProductsByCategory,
    searchProducts,
    addProduct,        // Thêm function mới
    updateProduct,     // Thêm function mới
    deleteProduct      // Thêm function mới
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};
