// src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import { userApi } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Kiểm tra token lưu trữ khi khởi động
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
    const role = localStorage.getItem('userRole');
    
    if (token && userId) {
      setIsAuthenticated(true);
      setUserRole(role || '');
      
      // Lấy thông tin người dùng
      userApi.getProfile(userId)
        .then(response => {
          if (response.data) {
            setCurrentUser(response.data);
          }
        })
        .catch(err => {
          console.error('Lỗi khi lấy thông tin người dùng:', err);
          // Nếu token không hợp lệ, đăng xuất
          if (err.response && err.response.status === 401) {
            logout();
          }
        })
        .finally(() => {
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  }, []);

  const login = async (email, password) => {
    try {
      setError(null);
      const response = await userApi.login({ email, password });
      const { token, user } = response.data;
      
      // Lưu thông tin xác thực
      localStorage.setItem('token', token);
      localStorage.setItem('userId', user.id);
      localStorage.setItem('userRole', user.role);
      
      setCurrentUser(user);
      setIsAuthenticated(true);
      setUserRole(user.role);
      
      return user;
    } catch (err) {
      console.error('Lỗi đăng nhập:', err);
      setError(err.response?.data?.message || 'Đăng nhập thất bại. Vui lòng thử lại.');
      throw err;
    }
  };

  const register = async (userData) => {
    try {
      setError(null);
      const response = await userApi.register(userData);
      const { token, user } = response.data;
      
      // Lưu thông tin xác thực
      localStorage.setItem('token', token);
      localStorage.setItem('userId', user.id);
      localStorage.setItem('userRole', user.role);
      
      setCurrentUser(user);
      setIsAuthenticated(true);
      setUserRole(user.role);
      
      return user;
    } catch (err) {
      console.error('Lỗi đăng ký:', err);
      setError(err.response?.data?.message || 'Đăng ký thất bại. Vui lòng thử lại.');
      throw err;
    }
  };

  const logout = () => {
    // Xóa thông tin xác thực
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    localStorage.removeItem('userRole');
    
    setCurrentUser(null);
    setIsAuthenticated(false);
    setUserRole('');
  };

  const updateProfile = async (data) => {
    try {
      setError(null);
      const response = await userApi.updateProfile(currentUser.id, data);
      setCurrentUser({...currentUser, ...response.data});
      return response.data;
    } catch (err) {
      console.error('Lỗi cập nhật thông tin người dùng:', err);
      setError(err.response?.data?.message || 'Cập nhật thông tin thất bại. Vui lòng thử lại.');
      throw err;
    }
  };

  const changePassword = async (currentPassword, newPassword) => {
    try {
      setError(null);
      await userApi.changePassword(currentUser.id, {
        currentPassword,
        newPassword
      });
    } catch (err) {
      console.error('Lỗi đổi mật khẩu:', err);
      setError(err.response?.data?.message || 'Đổi mật khẩu thất bại. Vui lòng thử lại.');
      throw err;
    }
  };

  const value = {
    currentUser,
    isAuthenticated,
    userRole,
    loading,
    error,
    login,
    register,
    logout,
    updateProfile,
    changePassword
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
