  updateItem: async (productId, quantity) => {
    try {
      return await apiClient.put(`/cart/items/${productId}`, { quantity });
    } catch (error) {
      console.error('Lỗi khi cập nhật số lượng sản phẩm trong giỏ hàng:', error);
      throw error;
    }
  },
  
  removeItem: async (productId) => {
    try {
      return await apiClient.delete(`/cart/items/${productId}`);
    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm khỏi giỏ hàng:', error);
      throw error;
    }
  },
  
  clearCart: async () => {
    try {
      return await apiClient.delete('/cart');
    } catch (error) {
      console.error('Lỗi khi xóa toàn bộ giỏ hàng:', error);
      throw error;
    }
  },
  
  // Wishlist API
  getWishlist: async () => {
    try {
      return await apiClient.get('/wishlist');
    } catch (error) {
      console.error('Lỗi khi lấy danh sách yêu thích:', error);
      return { data: [] };
    }
  },
  
  addToWishlist: async (productId) => {
    try {
      return await apiClient.post('/wishlist/items', { productId });
    } catch (error) {
      console.error('Lỗi khi thêm sản phẩm vào danh sách yêu thích:', error);
      throw error;
    }
  },
  
  removeFromWishlist: async (productId) => {
    try {
      return await apiClient.delete(`/wishlist/items/${productId}`);
    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm khỏi danh sách yêu thích:', error);
      throw error;
    }
  }
