// src/contexts/ProductContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { productApi, categoryApi } from '../services/api';

// Tạo context
const ProductContext = createContext();

// Hook để sử dụng context
export const useProduct = () => useContext(ProductContext);

// Provider component
export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Khởi tạo dữ liệu
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setIsLoading(true);
        
        // Lấy giỏ hàng và danh sách yêu thích từ localStorage
        const savedCart = localStorage.getItem('cart');
        const savedWishlist = localStorage.getItem('wishlist');
        
        if (savedCart) {
          setCart(JSON.parse(savedCart));
        }
        
        if (savedWishlist) {
          setWishlist(JSON.parse(savedWishlist));
        }
        
        // Lấy danh sách danh mục từ API
        try {
          const categoriesResponse = await categoryApi.getAll();
          if (categoriesResponse.data && categoriesResponse.data.length > 0) {
            setCategories(categoriesResponse.data);
          } else {
            setError('Không thể tải danh mục sản phẩm');
          }
        } catch (categoryError) {
          console.error('Không thể lấy danh mục từ API:', categoryError);
          setError('Không thể tải danh mục sản phẩm. Vui lòng tải lại trang.');
        }
        
        // Lấy danh sách sản phẩm từ API
        try {
          const productsResponse = await productApi.getAll();
          if (productsResponse.data && productsResponse.data.length > 0) {
            setProducts(productsResponse.data);
          } else {
            setError('Không tìm thấy sản phẩm nào');
          }
        } catch (productError) {
          console.error('Không thể lấy sản phẩm từ API:', productError);
          setError('Không thể tải sản phẩm. Vui lòng tải lại trang.');
        }
      } catch (err) {
        console.error('Lỗi khi khởi tạo dữ liệu:', err);
        setError('Không thể tải dữ liệu ban đầu. Vui lòng tải lại trang.');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchInitialData();
  }, []);

  // Lưu giỏ hàng vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading) {
      localStorage.setItem('cart', JSON.stringify(cart));
    }
  }, [cart, isLoading]);

  // Lưu danh sách yêu thích vào localStorage khi thay đổi
  useEffect(() => {
    if (!isLoading) {
      localStorage.setItem('wishlist', JSON.stringify(wishlist));
    }
  }, [wishlist, isLoading]);

  // Thêm sản phẩm mới
  const addProduct = async (product) => {
    try {
      setIsLoading(true);
      // Gửi dữ liệu lên server
      const response = await productApi.create(product);
      
      if (response && response.data) {
        // Cập nhật state với dữ liệu từ server
        setProducts([...products, response.data]);
        return response.data;
      } else {
        throw new Error('Không nhận được phản hồi từ server');
      }
    } catch (error) {
      console.error('Lỗi khi thêm sản phẩm:', error);
      setError('Không thể thêm sản phẩm. Vui lòng thử lại.');
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  // Cập nhật sản phẩm
  const updateProduct = async (id, updatedData) => {
    try {
      setIsLoading(true);
      // Gửi dữ liệu lên server
      const response = await productApi.update(id, updatedData);
      
      if (response && response.data) {
        const updatedProductData = response.data;
        
        // Cập nhật sản phẩm trong state
        const updatedProducts = products.map(product => 
          product.id === id ? updatedProductData : product
        );
        setProducts(updatedProducts);
        
        // Cập nhật sản phẩm trong giỏ hàng nếu có
        if (cart.some(item => item.id === id)) {
          const updatedCart = cart.map(item => 
            item.id === id ? { ...updatedProductData, quantity: item.quantity } : item
          );
          setCart(updatedCart);
        }
        
        // Cập nhật sản phẩm trong danh sách yêu thích nếu có
        if (wishlist.some(item => item.id === id)) {
          const updatedWishlist = wishlist.map(item => 
            item.id === id ? updatedProductData : item
          );
          setWishlist(updatedWishlist);
        }
        
        return updatedProductData;
      } else {
        throw new Error('Không nhận được phản hồi từ server');
      }
    } catch (error) {
      console.error('Lỗi khi cập nhật sản phẩm:', error);
      setError('Không thể cập nhật sản phẩm. Vui lòng thử lại.');
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  // Xóa sản phẩm
  const deleteProduct = async (id) => {
    try {
      setIsLoading(true);
      // Gửi yêu cầu xóa lên server
      await productApi.delete(id);
      
      // Xóa sản phẩm khỏi state
      setProducts(products.filter(product => product.id !== id));
      
      // Xóa sản phẩm khỏi giỏ hàng nếu có
      if (cart.some(item => item.id === id)) {
        setCart(cart.filter(item => item.id !== id));
      }
      
      // Xóa sản phẩm khỏi danh sách yêu thích nếu có
      if (wishlist.some(item => item.id === id)) {
        setWishlist(wishlist.filter(item => item.id !== id));
      }
      
      return id;
    } catch (error) {
      console.error('Lỗi khi xóa sản phẩm:', error);
      setError('Không thể xóa sản phẩm. Vui lòng thử lại.');
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  // Thêm sản phẩm vào giỏ hàng
  const addToCart = (product, quantity = 1) => {
    try {
      const existingItemIndex = cart.findIndex(item => item.id === product.id);
      
      if (existingItemIndex !== -1) {
        // Cập nhật số lượng nếu sản phẩm đã có trong giỏ hàng
        const updatedCart = [...cart];
        updatedCart[existingItemIndex].quantity += quantity;
        setCart(updatedCart);
      } else {
        // Thêm sản phẩm mới vào giỏ hàng
        setCart([...cart, { ...product, quantity }]);
      }
    } catch (error) {
      console.error('Lỗi khi thêm vào giỏ hàng:', error);
      setError('Không thể thêm sản phẩm vào giỏ hàng. Vui lòng thử lại.');
    }
  };

  // Cập nhật số lượng sản phẩm trong giỏ hàng
  const updateCartItem = (productId, quantity) => {
    try {
      if (quantity <= 0) {
        removeFromCart(productId);
        return;
      }
      
      const updatedCart = cart.map(item => 
        item.id === productId ? { ...item, quantity } : item
      );
      setCart(updatedCart);
    } catch (error) {
      console.error('Lỗi khi cập nhật giỏ hàng:', error);
      setError('Không thể cập nhật giỏ hàng. Vui lòng thử lại.');
    }
  };

  // Xóa sản phẩm khỏi giỏ hàng
  const removeFromCart = (productId) => {
    try {
      setCart(cart.filter(item => item.id !== productId));
    } catch (error) {
      console.error('Lỗi khi xóa khỏi giỏ hàng:', error);
      setError('Không thể xóa sản phẩm khỏi giỏ hàng. Vui lòng thử lại.');
    }
  };

  // Xóa toàn bộ giỏ hàng
  const clearCart = () => {
    try {
      setCart([]);
    } catch (error) {
      console.error('Lỗi khi xóa giỏ hàng:', error);
      setError('Không thể xóa giỏ hàng. Vui lòng thử lại.');
    }
  };

  // Thêm vào danh sách yêu thích
  const addToWishlist = (product) => {
    try {
      if (!wishlist.some(item => item.id === product.id)) {
        setWishlist([...wishlist, product]);
      }
    } catch (error) {
      console.error('Lỗi khi thêm vào danh sách yêu thích:', error);
      setError('Không thể thêm vào danh sách yêu thích. Vui lòng thử lại.');
    }
  };

  // Xóa khỏi danh sách yêu thích
  const removeFromWishlist = (productId) => {
    try {
      setWishlist(wishlist.filter(item => item.id !== productId));
    } catch (error) {
      console.error('Lỗi khi xóa khỏi danh sách yêu thích:', error);
      setError('Không thể xóa khỏi danh sách yêu thích. Vui lòng thử lại.');
    }
  };

  // Tính tổng giá trị giỏ hàng
  const getCartTotal = () => {
    try {
      return cart.reduce((total, item) => {
        const price = item.discount_price || item.price;
        return total + (price * item.quantity);
      }, 0);
    } catch (error) {
      console.error('Lỗi khi tính tổng giỏ hàng:', error);
      return 0;
    }
  };

  // Lấy sản phẩm theo ID
  const getProductById = async (id) => {
    try {
      setIsLoading(true);
      
      // Tìm sản phẩm trong state trước
      const product = products.find(product => product.id === parseInt(id));
      if (product) {
        setIsLoading(false);
        return Promise.resolve({ data: product });
      }
      
      // Nếu không tìm thấy, lấy từ API
      const response = await productApi.getById(id);
      if (response && response.data) {
        // Cập nhật vào state để lần sau không cần gọi API nữa
        const productExists = products.some(p => p.id === parseInt(id));
        if (!productExists) {
          setProducts([...products, response.data]);
        }
        return response;
      } else {
        throw new Error('Không tìm thấy sản phẩm');
      }
    } catch (err) {
      console.error(`Lỗi khi lấy thông tin sản phẩm ${id}:`, err);
      setError('Không thể tải thông tin sản phẩm. Vui lòng thử lại.');
      return Promise.reject(err);
    } finally {
      setIsLoading(false);
    }
  };

  // Lấy sản phẩm theo danh mục
  const getProductsByCategory = async (categoryId) => {
    try {
      setIsLoading(true);
      
      // Gọi API để lấy sản phẩm theo danh mục
      const response = await productApi.getByCategory(categoryId);
      return response;
    } catch (err) {
      console.error(`Lỗi khi lấy sản phẩm theo danh mục:`, err);
      setError('Không thể tải sản phẩm theo danh mục. Vui lòng thử lại.');
      return Promise.reject(err);
    } finally {
      setIsLoading(false);
    }
  };

  // Tìm kiếm sản phẩm
  const searchProducts = async (query) => {
    try {
      setIsLoading(true);
      
      // Gọi API để tìm kiếm sản phẩm
      const response = await productApi.search(query);
      return response;
    } catch (err) {
      console.error(`Lỗi khi tìm kiếm sản phẩm:`, err);
      setError('Không thể tìm kiếm sản phẩm. Vui lòng thử lại.');
      return Promise.reject(err);
    } finally {
      setIsLoading(false);
    }
  };

  // Các giá trị và hàm được cung cấp qua context
  const value = {
    products,
    categories,
    cart,
    wishlist,
    isLoading,
    error,
    addToCart,
    updateCartItem,
    removeFromCart,
    clearCart,
    addToWishlist,
    removeFromWishlist,
    getCartTotal,
    getProductById,
    getProductsByCategory,
    searchProducts,
    addProduct,
    updateProduct,
    deleteProduct
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};
