// src/utils/ServerStatus.js
import axios from 'axios';

export const checkServerStatus = async () => {
  try {
    // Kiểm tra xem server có đang chạy không
    const response = await axios.get('http://localhost:5000', {
      timeout: 5000
    });
    console.log('Server is running:', response.status);
    return {
      isRunning: true,
      message: 'Server đang hoạt động'
    };
  } catch (error) {
    console.error('Server check error:', error);
    
    if (error.code === 'ECONNREFUSED') {
      return {
        isRunning: false,
        message: 'Không thể kết nối đến server. Server có thể chưa được khởi động.'
      };
    }
    
    if (error.code === 'ETIMEDOUT') {
      return {
        isRunning: false,
        message: 'Kết nối đến server bị timeout.'
      };
    }
    
    return {
      isRunning: false,
      message: `Lỗi khi kiểm tra server: ${error.message}`
    };
  }
};
