// src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import { userApi } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [token, setToken] = useState(null);

  // Kiểm tra token và lấy thông tin người dùng khi component mount
  useEffect(() => {
    const verifyToken = async () => {
      try {
        // Lấy token từ session storage (hoặc có thể dùng http-only cookie)
        const storedToken = sessionStorage.getItem('token');
        
        if (!storedToken) {
          setLoading(false);
          return;
        }
        
        setToken(storedToken);
        
        // Gọi API để xác thực token
        const response = await userApi.verifyToken(storedToken);
        
        if (response.data.valid) {
          const user = response.data.user;
          setCurrentUser(user);
          setIsAuthenticated(true);
          setUserRole(user.role);
        } else {
          // Token không hợp lệ, xóa khỏi session storage
          sessionStorage.removeItem('token');
          setToken(null);
        }
      } catch (err) {
        console.error('Error verifying token:', err);
        sessionStorage.removeItem('token');
        setToken(null);
      } finally {
        setLoading(false);
      }
    };

    verifyToken();
  }, []);

  // Login function
  const login = async (email, password) => {
    try {
      setError(null);
      
      // Gọi API đăng nhập
      const response = await userApi.login(email, password);
      const { token, user } = response.data;
      
      // Lưu token vào session storage (hoặc có thể dùng http-only cookie)
      sessionStorage.setItem('token', token);
      setToken(token);
      
      // Cập nhật state
      setCurrentUser(user);
      setIsAuthenticated(true);
      setUserRole(user.role);
      
      return user;
    } catch (err) {
      console.error('Login error:', err);
      setError(err.response?.data?.message || 'Login failed');
      throw err;
    }
  };

  // Register function
  const register = async (userData) => {
    try {
      setError(null);
      const response = await userApi.register(userData);
      
      const { token, user } = response.data;
      
      // Lưu token vào session storage
      sessionStorage.setItem('token', token);
      setToken(token);
      
      // Cập nhật state
      setCurrentUser(user);
      setIsAuthenticated(true);
      setUserRole(user.role);
      
      return user;
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
      throw err;
    }
  };

  // Logout function
  const logout = async () => {
    try {
      // Gọi API logout nếu cần
      if (isAuthenticated) {
        await userApi.logout();
      }
      
      // Xóa token khỏi session storage
      sessionStorage.removeItem('token');
      setToken(null);
      
      // Cập nhật state
      setCurrentUser(null);
      setIsAuthenticated(false);
      setUserRole(null);
    } catch (err) {
      console.error('Logout error:', err);
    }
  };

  // Update profile
  const updateProfile = async (profileData) => {
    try {
      setError(null);
      
      if (!currentUser?.id) {
        throw new Error('User not authenticated');
      }
      
      const response = await userApi.updateProfile(currentUser.id, profileData);
      
      // Cập nhật state với thông tin mới
      const updatedUser = { ...currentUser, ...response.data };
      setCurrentUser(updatedUser);
      
      return updatedUser;
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile');
      throw err;
    }
  };

  // Change password
  const changePassword = async (currentPassword, newPassword) => {
    try {
      setError(null);
      
      if (!currentUser?.id) {
        throw new Error('User not authenticated');
      }
      
      await userApi.changePassword(currentUser.id, currentPassword, newPassword);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to change password');
      throw err;
    }
  };

  const value = {
    currentUser,
    isAuthenticated,
    userRole,
    loading,
    error,
    token,
    login,
    register,
    logout,
    updateProfile,
    changePassword
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
