// src/components/OrderHistory.js
import React, { useState, useEffect } from 'react';
import { Container, Table, Badge, Button, Card, Accordion } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Nav from './Nav';
import Footer from './Footer';

function OrderHistory() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Giả lập lấy dữ liệu đơn hàng
    const fetchOrders = () => {
      setLoading(true);
      
      // Tạo dữ liệu mẫu
      const mockOrders = [
        {
          id: 123456,
          date: '2023-05-15',
          status: 'delivered',
          total: 1299.99,
          items: [
            { id: 1, name: 'iPhone 15 Pro Max 256GB', price: 1199.99, quantity: 1, image: '/assets/img/products/iphone15promax.jpg' }
          ],
          shipping: {
            address: '123 Main St, City, Country',
            method: 'Express'
          },
          payment: {
            method: 'Credit Card',
            last4: '1234'
          }
        },
        {
          id: 123457,
          date: '2023-06-10',
          status: 'shipped',
          total: 1049.99,
          items: [
            { id: 2, name: 'MacBook Pro M3 14 inch', price: 1849.99, quantity: 1, image: '/assets/img/products/macbookpro.jpg' },
            { id: 3, name: 'AirPods Pro 2', price: 249.99, quantity: 1, image: '/assets/img/products/airpodspro.jpg' }
          ],
          shipping: {
            address: '456 Oak St, Another City, Country',
            method: 'Standard'
          },
          payment: {
            method: 'PayPal',
            email: 'user@example.com'
          }
        },
        {
          id: 123458,
          date: '2023-07-05',
          status: 'processing',
          total: 379.99,
          items: [
            { id: 5, name: 'Apple Watch Series 9', price: 379.99, quantity: 1, image: '/assets/img/products/applewatch.jpg' }
          ],
          shipping: {
            address: '789 Pine St, Third City, Country',
            method: 'Standard'
          },
          payment: {
            method: 'Credit Card',
            last4: '5678'
          }
        }
      ];
      
      setOrders(mockOrders);
      setLoading(false);
    };
    
    // Gọi hàm lấy dữ liệu
    fetchOrders();
    
    // Cuộn lên đầu trang
    window.scrollTo(0, 0);
  }, []);

  // Render trạng thái đơn hàng
  const renderOrderStatus = (status) => {
    switch (status) {
      case 'pending':
        return <Badge bg="warning" text="dark">Chờ xác nhận</Badge>;
      case 'processing':
        return <Badge bg="info">Đang xử lý</Badge>;
      case 'shipped':
        return <Badge bg="primary">Đang giao hàng</Badge>;
      case 'delivered':
        return <Badge bg="success">Đã giao hàng</Badge>;
      case 'cancelled':
        return <Badge bg="danger">Đã hủy</Badge>;
      default:
        return <Badge bg="secondary">{status}</Badge>;
    }
  };

  return (
    <>
      <Nav />
      <Container className="py-5 mt-5">
        <h2 className="mb-4">Lịch Sử Đơn Hàng</h2>
        
        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Đang tải...</span>
            </div>
            <p className="mt-3">Đang tải dữ liệu đơn hàng...</p>
          </div>
        ) : orders.length === 0 ? (
          <Card className="shadow-sm text-center py-5">
            <Card.Body>
              <i className="bi bi-bag-x" style={{ fontSize: '3rem', color: '#6c757d' }}></i>
              <h4 className="mt-3">Bạn chưa có đơn hàng nào</h4>
              <p className="text-muted">Hãy mua sắm và quay lại đây để xem lịch sử đơn hàng của bạn.</p>
              <Link to="/products" className="btn btn-primary mt-3">
                Mua sắm ngay
              </Link>
            </Card.Body>
          </Card>
        ) : (
          <Accordion defaultActiveKey={orders[0].id.toString()} className="shadow-sm">
            {orders.map((order) => (
              <Accordion.Item key={order.id} eventKey={order.id.toString()}>
                <Accordion.Header>
                  <div className="d-flex justify-content-between align-items-center w-100 me-3">
                    <div>
                      <span className="fw-bold">Đơn hàng #{order.id}</span>
                      <span className="text-muted ms-3">{new Date(order.date).toLocaleDateString()}</span>
                    </div>
                    <div className="d-flex align-items-center">
                      <span className="me-3">${order.total.toFixed(2)}</span>
                      {renderOrderStatus(order.status)}
                    </div>
                  </div>
                </Accordion.Header>
                <Accordion.Body>
                  <div className="mb-4">
                    <h5 className="mb-3">Chi tiết đơn hàng</h5>
                    <Table responsive borderless className="align-middle">
                      <thead className="bg-light">
                        <tr>
                          <th>Sản phẩm</th>
                          <th>Giá</th>
                          <th>Số lượng</th>
                          <th>Tổng</th>
                        </tr>
                      </thead>
                      <tbody>
                        {order.items.map((item) => (
                          <tr key={item.id}>
                            <td>
                              <div className="d-flex align-items-center">
                                <img 
                                  src={item.image} 
                                  alt={item.name} 
                                  style={{ width: '60px', height: '60px', objectFit: 'contain' }}
                                  className="me-3"
                                />
                                <div>
                                  <Link to={`/products/${item.id}`} className="text-decoration-none">
                                    {item.name}
                                  </Link>
                                </div>
                              </div>
                            </td>
                            <td>${item.price.toFixed(2)}</td>
                            <td>{item.quantity}</td>
                            <td>${(item.price * item.quantity).toFixed(2)}</td>
                          </tr>
                        ))}
                      </tbody>
                      <tfoot>
                        <tr>
                          <td colSpan="3" className="text-end fw-bold">Tổng cộng:</td>
                          <td className="fw-bold">${order.total.toFixed(2)}</td>
                        </tr>
                      </tfoot>
                    </Table>
                  </div>
                  
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <h5 className="mb-3">Thông tin giao hàng</h5>
                      <Card className="bg-light">
                        <Card.Body>
                          <p className="mb-1"><strong>Địa chỉ:</strong> {order.shipping.address}</p>
                          <p className="mb-0"><strong>Phương thức:</strong> {order.shipping.method}</p>
                        </Card.Body>
                      </Card>
                    </div>
                    <div className="col-md-6 mb-3">
                      <h5 className="mb-3">Thông tin thanh toán</h5>
                      <Card className="bg-light">
                        <Card.Body>
                          <p className="mb-1"><strong>Phương thức:</strong> {order.payment.method}</p>
                          {order.payment.last4 && (
                            <p className="mb-0"><strong>Thẻ:</strong> **** **** **** {order.payment.last4}</p>
                          )}
                          {order.payment.email && (
                            <p className="mb-0"><strong>Email:</strong> {order.payment.email}</p>
                          )}
                        </Card.Body>
                      </Card>
                    </div>
                  </div>
                  
                  <div className="text-end mt-3">
                    {order.status === 'delivered' && (
                      <Button variant="outline-primary" className="me-2">
                        Đánh giá sản phẩm
                      </Button>
                    )}
                    {(order.status === 'pending' || order.status === 'processing') && (
                      <Button variant="outline-danger" className="me-2">
                        Hủy đơn hàng
                      </Button>
                    )}
                    <Button variant="primary">
                      Liên hệ hỗ trợ
                    </Button>
                  </div>
                </Accordion.Body>
              </Accordion.Item>
            ))}
          </Accordion>
        )}
      </Container>
      <Footer />
    </>
  );
}

export default OrderHistory;
