// src/components/HomePage.js
import React from 'react';
import Nav from './Nav';
import Header from './Header';
import Products from './Products';
import Contact from './Contact';
import Footer from './Footer';
import { useEffect, useState } from 'react';
import { articleApi } from '../services/api';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Thêm vào phần useEffect trong HomePage.js
useEffect(() => {
  // Kiểm tra xem đã có dữ liệu sản phẩm trong localStorage chưa
  const storedProducts = localStorage.getItem('articles');
  
  if (!storedProducts) {
    // Nếu chưa có, thêm dữ liệu mẫu từ productData
    import('../data/productData').then(module => {
      const productData = module.default;
      localStorage.setItem('articles', JSON.stringify(productData));
      setProducts(productData);
      setLoading(false);
    });
  } else {
    // Nếu đã có, sử dụng dữ liệu từ localStorage
    setProducts(JSON.parse(storedProducts));
    setLoading(false);
  }
  
  // Khởi tạo đối tượng comments trống nếu chưa có
  if (!localStorage.getItem('comments')) {
    localStorage.setItem('comments', JSON.stringify({}));
  }
}, []);

  return (
    <div id="page-top">
      <Nav />
      <Header />
      <Products 
        products={products} 
        loading={loading} 
        error={error} 
      />
      <Contact />
      <Footer />
    </div>
  );
}

export default HomePage;
