// src/App.js
import React, { useEffect } from 'react'; // Thêm import useEffect
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import ProductDetail from './components/ProductDetail';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import OrderHistory from './components/OrderHistory';
import Login from './components/Login';
import AdminDashboard from './components/AdminDashboard';
import PrivateRoute from './components/PrivateRoute';
import ScrollToTop from './components/ScrollToTop';
import { ProductProvider } from './contexts/ProductContext';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { testApiConnection } from './utils/TestConnection'; // Thêm import testApiConnection
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Component NotFound tạm thời
const NotFound = () => (
  <div className="container mt-5 py-5 text-center">
    <h1>404 - Không tìm thấy trang</h1>
    <p>Trang bạn đang tìm không tồn tại hoặc đã bị xóa.</p>
    <a href="/" className="btn btn-primary">Quay về trang chủ</a>
  </div>
);

// Component Profile tạm thời
const Profile = () => (
  <div className="container mt-5 py-5">
    <h1>Trang cá nhân</h1>
    <p>Tính năng đang được phát triển.</p>
  </div>
);

// Component Register tạm thời
const Register = () => (
  <div className="container mt-5 py-5">
    <h1>Đăng ký tài khoản</h1>
    <p>Tính năng đang được phát triển.</p>
  </div>
);

// Main App component
function AppContent() {
  const { isAuthenticated, userRole } = useAuth();

  return (
    <ProductProvider>
      <Router>
        <ScrollToTop />
        <Routes>
          {/* Public pages */}
          <Route path="/" element={<HomePage />} />
          <Route path="/products" element={<ProductList />} />
          <Route path="/products/category/:category" element={<ProductList />} />
          <Route path="/products/search" element={<ProductList />} />
          <Route path="/products/:id" element={<ProductDetail />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/register" element={
            isAuthenticated ? <Navigate to="/" /> : <Register />
          } />
          <Route path="/login" element={
            isAuthenticated ? 
              (userRole === 'admin' ? <Navigate to="/admin" /> : <Navigate to="/" />) : 
              <Login />
          } />
          
          {/* Protected routes - require authentication */}
          <Route path="/checkout" element={
            <PrivateRoute>
              <Checkout />
            </PrivateRoute>
          } />
          
          <Route path="/order-history" element={
            <PrivateRoute>
              <OrderHistory />
            </PrivateRoute>
          } />
          
          <Route path="/profile" element={
            <PrivateRoute>
              <Profile />
            </PrivateRoute>
          } />
          
          {/* Admin routes - require admin role */}
          <Route path="/admin/*" element={
            <PrivateRoute requireAdmin={true}>
              <AdminDashboard />
            </PrivateRoute>
          } />
          
          {/* Fallback route */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </Router>
    </ProductProvider>
  );
}

// Wrap with Auth Provider
function App() {
  useEffect(() => {
    testApiConnection();
  }, []);
  
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
