// src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Create axios instance with base configuration
const apiClient = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add request interceptor to include auth token when available
apiClient.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Product API
export const productApi = {
  getAll: async (params) => {
    try {
      return await apiClient.get('/products', { params });
    } catch (error) {
      console.error('Error fetching products:', error);
      return { data: [] };
    }
  },
  
  getById: async (id) => {
    try {
      return await apiClient.get(`/products/${id}`);
    } catch (error) {
      console.error(`Error fetching product ${id}:`, error);
      return { data: null };
    }
  },
  
  getByCategory: async (categoryId) => {
    try {
      return await apiClient.get(`/products/category/${categoryId}`);
    } catch (error) {
      console.error(`Error fetching products for category ${categoryId}:`, error);
      return { data: [] };
    }
  },
  
  getRelated: async (id) => {
    try {
      return await apiClient.get(`/products/${id}/related`);
    } catch (error) {
      console.error(`Error fetching related products for ${id}:`, error);
      return { data: [] };
    }
  },
  
  search: async (query) => {
    try {
      return await apiClient.get(`/products/search`, { params: { query } });
    } catch (error) {
      console.error(`Error searching products:`, error);
      return { data: [] };
    }
  },
  
  create: async (data) => {
    try {
      return await apiClient.post('/products', data);
    } catch (error) {
      console.error('Error creating product:', error);
      throw error;
    }
  },
  
  update: async (id, data) => {
    try {
      return await apiClient.put(`/products/${id}`, data);
    } catch (error) {
      console.error(`Error updating product ${id}:`, error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      return await apiClient.delete(`/products/${id}`);
    } catch (error) {
      console.error(`Error deleting product ${id}:`, error);
      throw error;
    }
  }
};

// Review API
export const reviewApi = {
  getByProductId: async (productId) => {
    try {
      return await apiClient.get(`/reviews/${productId}`);
    } catch (error) {
      console.error(`Error fetching reviews for product ${productId}:`, error);
      return { data: [] };
    }
  },
  
  add: async (productId, data) => {
    try {
      return await apiClient.post(`/reviews/${productId}`, data);
    } catch (error) {
      console.error(`Error adding review to product ${productId}:`, error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      return await apiClient.delete(`/reviews/${id}`);
    } catch (error) {
      console.error(`Error deleting review ${id}:`, error);
      throw error;
    }
  },
  
  getAll: async () => {
    try {
      return await apiClient.get('/reviews');
    } catch (error) {
      console.error('Error fetching all reviews:', error);
      return { data: [] };
    }
  }
};

// Category API
export const categoryApi = {
  getAll: async () => {
    try {
      return await apiClient.get('/categories');
    } catch (error) {
      console.error('Error fetching categories:', error);
      return { data: [] };
    }
  },
  
  getById: async (id) => {
    try {
      return await apiClient.get(`/categories/${id}`);
    } catch (error) {
      console.error(`Error fetching category ${id}:`, error);
      return { data: null };
    }
  },
  
  create: async (data) => {
    try {
      return await apiClient.post('/categories', data);
    } catch (error) {
      console.error('Error creating category:', error);
      throw error;
    }
  },
  
  update: async (id, data) => {
    try {
      return await apiClient.put(`/categories/${id}`, data);
    } catch (error) {
      console.error(`Error updating category ${id}:`, error);
      throw error;
    }
  },
  
  delete: async (id) => {
    try {
      return await apiClient.delete(`/categories/${id}`);
    } catch (error) {
      console.error(`Error deleting category ${id}:`, error);
      throw error;
    }
  }
};

// Order API
export const orderApi = {
  getAll: async () => {
    try {
      return await apiClient.get('/orders');
    } catch (error) {
      console.error('Error fetching orders:', error);
      return { data: [] };
    }
  },
  
  getById: async (id) => {
    try {
      return await apiClient.get(`/orders/${id}`);
    } catch (error) {
      console.error(`Error fetching order ${id}:`, error);
      return { data: null };
    }
  },
  
  getUserOrders: async (userId) => {
    try {
      return await apiClient.get(`/orders/user/${userId}`);
    } catch (error) {
      console.error(`Error fetching user orders:`, error);
      return { data: [] };
    }
