  create: async (data) => {
    try {
      return await apiClient.post('/orders', data);
    } catch (error) {
      console.error('Error creating order:', error);
      throw error;
    }
  },
  
  updateStatus: async (id, status) => {
    try {
      return await apiClient.put(`/orders/${id}/status`, { status });
    } catch (error) {
      console.error(`Error updating order status ${id}:`, error);
      throw error;
    }
  },
  
  getStats: async () => {
    try {
      return await apiClient.get('/orders/stats');
    } catch (error) {
      console.error('Error fetching order stats:', error);
      return { data: {} };
    }
  },
  
  getCustomers: async () => {
    try {
      return await apiClient.get('/orders/customers');
    } catch (error) {
      console.error('Error fetching customers:', error);
      return { data: [] };
    }
  }
};

// User API
export const userApi = {
  register: async (userData) => {
    try {
      return await apiClient.post('/users/register', userData);
    } catch (error) {
      console.error('Error registering user:', error);
      throw error;
    }
  },
  
  login: async (credentials) => {
    try {
      return await apiClient.post('/users/login', credentials);
    } catch (error) {
      console.error('Error logging in:', error);
      throw error;
    }
  },
  
  getProfile: async (id) => {
    try {
      return await apiClient.get(`/users/${id}`);
    } catch (error) {
      console.error(`Error fetching user profile:`, error);
      return { data: null };
    }
  },
  
  updateProfile: async (id, data) => {
    try {
      return await apiClient.put(`/users/${id}`, data);
    } catch (error) {
      console.error(`Error updating user profile:`, error);
      throw error;
    }
  },
  
  changePassword: async (id, data) => {
    try {
      return await apiClient.put(`/users/${id}/password`, data);
    } catch (error) {
      console.error(`Error changing password:`, error);
      throw error;
    }
  },
  
  getAllUsers: async () => {
    try {
      return await apiClient.get('/users');
    } catch (error) {
      console.error('Error fetching all users:', error);
      return { data: [] };
    }
  }
};

export default {
  product: productApi,
  review: reviewApi,
  category: categoryApi,
  order: orderApi,
  user: userApi
};
