// Trong file Nav.js, cập nhật phần dropdown danh mục
import categories from '../data/categories';  // Thêm dòng này

// Thay thế phần dropdown danh mục hiện tại bằng đoạn code sau:
<Dropdown as={BootstrapNav.Item}>
  <Dropdown.Toggle as={Link} to="#" className="nav-link px-lg-3 py-3 py-lg-4 d-flex align-items-center" style={{ cursor: 'pointer' }}>
    <FaTag className="me-1 d-none d-md-inline" /> Danh Mục
  </Dropdown.Toggle>
  <Dropdown.Menu>
    {categories.map(category => (
      <Dropdown.Item 
        key={category.id} 
        as={Link} 
        to={`/products/category/${category.name}`}
      >
        {category.name}
      </Dropdown.Item>
    ))}
  </Dropdown.Menu>
</Dropdown>
