// src/contexts/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import { userApi } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Kiểm tra token lưu trữ khi khởi động
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');
      const isAuth = localStorage.getItem('isAuthenticated') === 'true';
      
      if (token && userId && isAuth) {
        try {
          setLoading(true);
          // Gọi API để kiểm tra token và lấy thông tin người dùng
          const response = await userApi.getProfile(userId);
          
          if (response.data) {
            setCurrentUser(response.data);
            setIsAuthenticated(true);
            setUserRole(response.data.role || '');
          } else {
            // Token không hợp lệ, đăng xuất
            logout();
          }
        } catch (err) {
          console.error('Lỗi khi lấy thông tin người dùng:', err);
          // Nếu token không hợp lệ, đăng xuất
          logout();
        } finally {
          setLoading(false);
        }
      } else {
        setLoading(false);
      }
    };
    
    checkAuth();
  }, []);

  const login = async (email, password) => {
    try {
      setError(null);
      setLoading(true);
      
      // Gọi API đăng nhập
      const response = await userApi.login({ email, password });
      
      if (!response.data) {
        throw new Error('Không nhận được phản hồi từ server');
      }
      
      const { token, user } = response.data;
      
      if (!token || !user) {
        throw new Error('Dữ liệu đăng nhập không hợp lệ');
      }
      
      // Lưu thông tin xác thực
      localStorage.setItem('token', token);
      localStorage.setItem('userId', user.id);
      localStorage.setItem('userRole', user.role || 'customer');
      localStorage.setItem('isAuthenticated', 'true');
      
      setCurrentUser(user);
      setIsAuthenticated(true);
      setUserRole(user.role || 'customer');
      
      return user;
    } catch (err) {
      console.error('Lỗi đăng nhập:', err);
      setError(err.response?.data?.message || 'Đăng nhập thất bại. Vui lòng thử lại.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const register = async (userData) => {
    try {
      setError(null);
      setLoading(true);
      
      const response = await userApi.register(userData);
      
      if (!response.data) {
        throw new Error('Không nhận được phản hồi từ server');
      }
      
      const { token, user } = response.data;
      
      if (!token || !user) {
        throw new Error('Dữ liệu đăng ký không hợp lệ');
      }
      
      // Lưu thông tin xác thực
      localStorage.setItem('token', token);
      localStorage.setItem('userId', user.id);
      localStorage.setItem('userRole', user.role || 'customer');
      localStorage.setItem('isAuthenticated', 'true');
      
      setCurrentUser(user);
      setIsAuthenticated(true);
      setUserRole(user.role || 'customer');
      
      return user;
    } catch (err) {
      console.error('Lỗi đăng ký:', err);
      setError(err.response?.data?.message || 'Đăng ký thất bại. Vui lòng thử lại.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    // Xóa thông tin xác thực
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    localStorage.removeItem('userRole');
    localStorage.removeItem('isAuthenticated');
    
    setCurrentUser(null);
    setIsAuthenticated(false);
    setUserRole('');
  };

  const updateProfile = async (data) => {
    try {
      setError(null);
      setLoading(true);
      
      if (!currentUser?.id) {
        throw new Error('Người dùng chưa đăng nhập');
      }
      
      const response = await userApi.updateProfile(currentUser.id, data);
      
      // Cập nhật thông tin người dùng trong state
      setCurrentUser({...currentUser, ...response.data});
      
      return response.data;
    } catch (err) {
      console.error('Lỗi cập nhật thông tin người dùng:', err);
      setError(err.response?.data?.message || 'Cập nhật thông tin thất bại. Vui lòng thử lại.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const changePassword = async (currentPassword, newPassword) => {
    try {
      setError(null);
      setLoading(true);
      
      if (!currentUser?.id) {
        throw new Error('Người dùng chưa đăng nhập');
      }
      
      await userApi.changePassword(currentUser.id, {
        currentPassword,
        newPassword
      });
      
      return true;
    } catch (err) {
      console.error('Lỗi đổi mật khẩu:', err);
      setError(err.response?.data?.message || 'Đổi mật khẩu thất bại. Vui lòng thử lại.');
      throw err;
    } finally {
      setLoading(false);
    }
  };
  
  // Thêm hàm yêu cầu đặt lại mật khẩu
  const requestPasswordReset = async (email) => {
    try {
      setError(null);
      setLoading(true);
      
      await userApi.requestPasswordReset(email);
      return true;
    } catch (err) {
      console.error('Lỗi yêu cầu đặt lại mật khẩu:', err);
      setError(err.response?.data?.message || 'Yêu cầu đặt lại mật khẩu thất bại. Vui lòng thử lại.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const value = {
    currentUser,
    isAuthenticated,
    userRole,
    loading,
    error,
    login,
    register,
    logout,
    updateProfile,
    changePassword,
    requestPasswordReset
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
